window.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".menu"),t=document.querySelectorAll(".menu_item"),n=document.querySelector(".menu_btn");n.addEventListener("click",()=>{n.classList.toggle("menu_btn_active"),e.classList.toggle("menu_active")}),t.forEach(t=>{t.addEventListener("click",()=>{n.classList.toggle("menu_btn_active"),e.classList.toggle("menu_active")})})});